    setTodos(arr);
  };

  const editTodo = (id, text) => {
    const arr = todos.map(item => {
      if (item.id === id) {
        return { id: id, task: text };
      }
      return item;
    });

    setTodos(arr);
  };
  return (
    <div>
      <h1>Todo App</h1>
      <Form onSubmit={addTask} />
      <List todos={todos} deleteTodo={deleteTodo} editTodo={editTodo} />
    </div>//
  );
}
